var $ = jQuery.noConflict();
jQuery(function () {
	isElementExist(".nav-drop", initSmartMenu);
	isElementExist(".swiper-article", swiperArtilceHome);
	isElementExist(".swiper-programm", swiperProgrammHome);
	isElementExist(".responsiveTabs", tabsToAcordionInit);
	isElementExist(".header-search-block", initOpenCloseSearch);
	isElementExist(".accordion", initAccordion);
	isElementExist(".fixed-block", initFixedScrollBlock);
	initCustomForms();
	initAnchors();
	initSearch();
});

//-------- -------- -------- --------
//-------- js custom start
//-------- -------- -------- --------

// Helper if element exist then call function
function isElementExist(_el, _cb) {
	var elem = document.querySelector(_el);

	if (document.body.contains(elem)) {
		try {
			_cb();
		} catch (e) {
			console.log(e);
		}
	}
}

// initialize custom form elements (checkbox, radio, select) https://github.com/w3co/jcf
function initCustomForms() {
	jcf.setOptions('Select', {
		maxVisibleItems: 5, // visible dropdown items before scroll appear
		wrapNative: false,
	});
	jcf.replaceAll();
}

// smart menu init
function initSmartMenu() {
	var distanceBetweenMenuAndNav = jQuery(".header-menu-wrapper").offset().top + jQuery(".header-menu-wrapper").innerHeight() - jQuery(".nav").offset().top - jQuery(".nav").innerHeight() - 10;
	jQuery(".header-menu").smartmenus({
		subIndicators: false,
		collapsibleBehavior: "accordion",
		mainMenuSubOffsetY: distanceBetweenMenuAndNav
	});

	// changed date attribute to a class (need to reverse last item menu)
	// jQuery('.header-menu').children().last().addClass('nav-sm-reverse');

	jQuery("body").mobileNav({
		menuActiveClass: "nav-active",
		menuOpener: ".nav-opener",
		hideOnClickOutside: false,
		menuDrop: ".nav-drop"
	}), "ontouchstart" in document.documentElement ||
		jQuery(window).on("resize orientationchange", function () {
			jQuery("body").removeClass("nav-active"), $.SmartMenus.hideAll();
		});

	jQuery("body").mobileNav({
		menuActiveClass: "nav-sub-active",
		menuOpener: ".nav-search",
		hideOnClickOutside: false,
		menuDrop: ".header-search-mob"
	});
}

// function init
function tabsToAcordionInit() {
	// all option see https://github.com/jellekralt/Responsive-Tabs
	let option = {};
	$('.responsiveTabs').responsiveTabs({ option });
}

// function swiper init
function swiperArtilceHome() {
	const swiper = new Swiper(".swiper-article", {
		spaceBetween: 20,
		slidesPerView: 1.15,
		navigation: {
			nextEl: ".swiper-button-next-s1",
			prevEl: ".swiper-button-prev-s1",
		},
		breakpoints: {
			769: {
				slidesPerView: 1.8
			},
			1025: {
				slidesPerView: 2.8
			}
		}
	});
}

// function swiper init
function swiperProgrammHome() {
	const swiper = new Swiper(".swiper-programm", {
		spaceBetween: 10,
		slidesPerView: 1.6,
		navigation: {
			nextEl: ".swiper-button-next-s2",
			prevEl: ".swiper-button-prev-s2",
		},
		breakpoints: {
			480: {
				slidesPerView: 2.3
			},
			769: {
				spaceBetween: 20,
				slidesPerView: 2.8
			},
			1025: {
				slidesPerView: 3.8
			}
		}
	});
}

// function OpenClose for header-search-block init
function initOpenCloseSearch() {
	jQuery('.header-search-block').openClose({
		opener: '.header-search-btn:not(._mob)',
		slider: '.header-search',
		hideOnClickOutside: true,
		effect: 'none',
	});
}

// accordion menu init
function initAccordion() {
	jQuery('.accordion').slideAccordion({
		opener: '.opener',
		slider: '.slide',
		animSpeed: 300
	});
}

// initialize fixed blocks on scroll
function initFixedScrollBlock() {
	jQuery('.fixed-block').fixedScrollBlock({
		slideBlock: '.fixed-item',
		positionType: 'absolute',
		extraTop: 10,
		animDelay: 0,
		animSpeed: 0,
	});
}

// initialize smooth anchor links
function initAnchors() {
	new SmoothScroll({
		anchorLinks: 'a[href^="#"]:not([href="#"]), .anchor-link, .jcf-option-anchor-link',
		extraOffset: 0,
		activeClasses: 'link',
		wheelBehavior: 'none'
	});

	jQuery('.select-page select').on('change', function () {
		var $el = jQuery(jQuery(this).find(':selected').attr('class'))
		jQuery('html,body').animate({
			scrollTop: $el.offset().top
		}, 500);
	});
}

function initSearch() {
	jQuery('.search-field').on('keyup', function () {
		var $this = $(this),
			val = $this.val();
		if (val.length >= 1) {
			jQuery('.result-search-block').show(10);
		} else {
			jQuery('.result-search-block').hide(10);
		}
	});
}

